import React from 'react';
import { StyleSheet, TouchableOpacity, ViewStyle } from 'react-native';
import { Icon, Layout, useTheme } from '@ui-kitten/components';
import { useLayout } from 'hooks';

interface TabBarProps {
  style?: ViewStyle;
}

const TabBar: React.FC<TabBarProps> = ({ style }) => {
  const theme = useTheme();
  const { bottom } = useLayout();

  const [index, setIndex] = React.useState<number>(0);

  const icons = ['chart-pie-slice', 'cardholder', 'code-sand-box-logo', 'user-circle'];

  return (
    <Layout
      level="1"
      style={[styles.tabView, { height: bottom + 56, paddingBottom: bottom }, style]}>
      {icons.map((i, idx) => {
        return (
          <TouchableOpacity
            key={idx}
            activeOpacity={0.7}
            onPress={() => setIndex(idx)}
            style={styles.tab}>
            <Icon
              pack="assets"
              name={i}
              style={[
                styles.icon,
                {
                  tintColor:
                    index === idx ? theme['color-primary-500'] : theme['text-platinum-color'],
                },
              ]}
            />
          </TouchableOpacity>
        );
      })}
    </Layout>
  );
};

export default TabBar;

const styles = StyleSheet.create({
  tabView: {
    flexDirection: 'row',
    overflow: 'hidden',
    padding: 8,
    borderTopLeftRadius: 16,
    borderTopRightRadius: 16,
    position: 'absolute',
    left: 0,
    right: 0,
    bottom: 0,
  },
  tab: {
    flex: 1,
    justifyContent: 'center',
    alignItems: 'center',
  },
  icon: {
    width: 20,
    height: 20,
  },
});
